import React from "react";
import "./App.css";
import friends from "./data";

class App extends React.Component {
  state = {
    friends,
    clicked: [],
    score: 0
  };
  renderFriends = () => {
    return this.state.friends.map(friend => (
      <img
        className="friend"
        value={friend.name}
        src={friend.image}
        onClick={this.handleClick}
      />
    ));
  };
  handleClick = e => {
    var clickFriend = e.currentTarget.getAttribute("value");
    console.log(clickFriend);
    if (this.state.clicked.includes(clickFriend)) {
      console.log("you loss");
    } else {
      this.setState({
        clicked: this.state.clicked.concat(clickFriend),
        sore: this.state.score + 1
        // console.log(this.state.score);
      });
    }
    this.shuffleFriends();
  };
  shuffleFriends = () => {
    this.setState({
      friends: this.state.friends.sort(() => Math.random() - 0.5)
    });
  };
  render() {
    return <div className="App">{this.renderFriends()}</div>;
  }
}

export default App;
